mapper.update:
UPDATE attendance
SET
        clock_in = #{clockIn},
        clock_out = #{clockOut},
        status = #{status}
WHERE attendance_no = #{attendanceNo}

mapper.select:
SELECT a.ATTENDANCE_NO, a.EMP_NO, m.MEM_NAME, m.MEM_NAME AS EMP_NAME, a.CLOCK_IN, a.CLOCK_OUT, TRUNC(a.CLOCK_IN) AS WORKDAY, a.STATUS, CASE WHEN a.CLOCK_OUT IS NOT NULL THEN FLOOR((a.CLOCK_OUT - a.CLOCK_IN) * 24) || '시간 ' || FLOOR(MOD((a.CLOCK_OUT - a.CLOCK_IN) * 24 * 60, 60)) || '분' ELSE NULL END AS WORKTIME FROM ATTENDANCE a JOIN MEMBER m ON a.EMP_NO = m.EMP_NO WHERE m.STORE_ID = #{storeId}
AND m.MEM_NAME = #{empName}
AND TRUNC(a.CLOCK_IN) BETWEEN #{startDate} AND #{endDate}
ORDER BY a.ATTENDANCE_NO DESC

SELECT COUNT(*)
FROM ATTENDANCE
WHERE EMP_NO = #{empNo}

SELECT COUNT(*)
FROM ATTENDANCE A
JOIN MEMBER M ON A.EMP_NO = M.EMP_NO
WHERE M.STORE_ID = #{storeId}

    AND M.MEM_NAME = #{empName}


    AND TRUNC(A.CLOCK_IN) >= #{startDate}


    AND TRUNC(A.CLOCK_IN) <= #{endDate}

SELECT A.ATTENDANCE_NO, A.EMP_NO, M.MEM_NAME, A.CLOCK_IN, A.CLOCK_OUT, TRUNC(A.CLOCK_IN) AS WORKDAY, A.STATUS, CASE WHEN A.CLOCK_OUT IS NOT NULL THEN FLOOR((A.CLOCK_OUT - A.CLOCK_IN) * 24) || '시간 ' || FLOOR(MOD((A.CLOCK_OUT - A.CLOCK_IN) * 24 * 60, 60)) || '분' ELSE NULL END AS WORKTIME FROM ATTENDANCE A JOIN MEMBER M ON A.EMP_NO = M.EMP_NO
M.STORE_ID = #{storeId}
AND M.MEM_NAME = #{empName}
AND TRUNC(A.CLOCK_IN) >= #{startDate}
AND TRUNC(A.CLOCK_IN) <= #{endDate}
AND M.POSITION = 'employee'
ORDER BY A.CLOCK_OUT DESC

SELECT COUNT(DISTINCT A.emp_no)
FROM ATTENDANCE A
JOIN MEMBER M ON A.emp_no = M.emp_no
WHERE M.store_id = #{storeId}
AND A.status = #{status}
AND TRUNC(A.clock_in) = TRUNC(SYSDATE)
