mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEM_ID = #{memId}

mapper.update:
UPDATE MEMBER
SET STATUS = 'D'
WHERE MEM_ID = #{memId}

UPDATE STORE
SET STATUS = 'Y'
WHERE STORE_ID = #{storeId}

UPDATE MEMBER
SET POSITION = 'manager'
WHERE MEM_ID = #{memId}

UPDATE STORE
SET STATUS = 'D'
WHERE STORE_ID = #{storeId}

UPDATE MEMBER
SET PHONE = #{phone}
WHERE MEM_ID = #{memId}

UPDATE MEMBER
SET MEM_PWD = #{newPwd}
WHERE MEM_ID = #{memId}
AND MEM_PWD = #{memPwd}
